    public async void ConnectWallet()
    {
        // Configure the connection
        var connection = new WalletConnection(
          provider: WalletProvider.Metamask,       // The wallet provider you want to connect to (Required)
          chainId: 11155111                               // The chain you want to connect to (Required)
        );

        // Connect the wallet
        address = await SDKManager.Instance.SDK.wallet.Connect(connection);

        Text1.text = "";
        UserAddressText.text = address;
        ConnectButton.SetActive(false);

        await CheckBalance();
    }

    public async Task CheckBalance()
    {
        Contract contract = SDKManager.Instance.SDK.GetContract(contractAddress, contractABI);

        string balance = await contract.ERC721.Balance();
        
        float balanceFloat = float.Parse(balance);

        if (balanceFloat == 0)
        {
            Text2.text = "You don't have access to the Metaverse!";
            return;
        }

        Text2.text = "You have access to the metaverse! Press the button to start";
        StartButton.SetActive(true);

        string DTHash = await contract.Read<string>("getDTMetadata");
        PlayerPrefs.SetString("DTHash", DTHash);

        string RecordsHash = await contract.Read<string>("getDTRecords");
        PlayerPrefs.SetString("RecordsHash", RecordsHash);
    }

    public void EnterGame()
    {
        SceneManager.LoadScene("Main");
    }

